replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'I had always wondered what this place looked like beyond the wall... Now that I\'ve been there I must admit I am disappointed. Perhaps once you have seen one kingdom you\'ve seen them all$B$B You looking to do some work around here, or just admire the scenery?');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'Looking for something? You\'d better spit it out.');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'You can\'t just sneak up on people you know that?! You almost put me into my second grave.$B$BCan\'t you see that I am occupied here? Busy, engaged, distracted, preoccupied, immersed, absorbed, working?$B$BHow many other ways can I describe this to you before you understand.');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'Can you believe it, the Scourge, here in Gilneas! No matter where I travel their taint seems to be present.$B$BThe Greymane wall was breached some time in the past. From my studies these are the remnants of a former scourge invasion. This is a good opportunity, foolish to waste. There is still much we can learn from these lands, and I must uncover all that I can while I have the opportunity.');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'Dark magic flows through my veins, I am reinvigorated to stand once more. My life was mired with deceit, anguish, and murder, perhaps now I can enact these miseries on those that wrought me to an early grave.');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'Why are you staring? Have you never seen a soldier before?');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'The land of Gilneas is certainly wounded and scarred, but it is not rotten from the inside out like Tirisfal. Any chance to extend our influence here will take considerable effort.');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'We have been sent to monitor the situation in Gilneas, things here are... Far more interesting than I would have imagined.$B$BPerhaps we can be a more instrumental part of future history.');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'During my life it was I who looked after this church. I was murdered in cold blood by those that bare the emblem of this kingdom. It appears that I have been awoken from my slumber to serve a new purpose, and serve this purpose I shall.$B$BYou will find no hostilities here, I may be Gilnean but I offer a chance to find solace inside the walls of my parish.$B$BShould you need to rest, feel free to ask.');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'My father is a skilled hunter, he is able to kill giant wolfs and then make them into food!');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
