REPLACE INTO `mangos_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (50306, 'Happy Chinese New Year!', NULL, NULL, NULL, '祝大家春节快乐！', NULL, NULL, NULL, NULL);
REPLACE INTO `mangos_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (50307, 'Dear players,\n\nWishing you joy, health, and happiness in the upcoming Chinese New Year! Your support fuels our growth and commitment to delivering exciting gaming experiences.\n\nLet\'s continue the adventure together and create new legends!\n\nHappy Chinese New Year!', NULL, NULL, NULL, '亲爱的玩家们，\n\n在这辉煌的新春佳节来临之际，乌龟服团队全体成员向您献上最诚挚的祝福！祝愿您在新的一年里，幸福安康，阖家欢乐，万事如意！\n\n新年代表着新的开始和希望，让我们共同迎接挑战，创造更多精彩的游戏体验。在过去的岁月里，感谢每一位玩家对我们游戏的支持和厚爱，正是因为您们的陪伴，我们才能不断进步和成长。\n\n乌龟服团队将继续努力，为您带来更多惊喜和乐趣，我们愿意与您共同分享游戏的喜悦和激情。让我们一起开启新的冒险旅程，创造属于我们的传奇！\n\n在这个特别的时刻，让我们共同祝福中国新年，让我们的友谊和团结像一条长龙般延绵不绝，共同度过一个美好而难忘的新年！\n\n祝愿大家新年快乐，万事如意！\n\n乌龟服团队 敬上', NULL, NULL, NULL, NULL);
