set @gossip_menu_id = 41617; set @magic_number = 61927;
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'Listen here pal, if you want to make the real money I have the secrets for you. You think gold is valueable? Try turning it into luxury pieces of jewelry!$B$BI\'ve had some of my creations go for insane profits at auction, and you can do the same. All you gotta do is follow my easy steps and you too can hit big in the world of jewelcrafting!');
