replace into `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES
(30224, 'Hold the Rod of Remembrance in front of it.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0),
(30225, 'You will not relinquish him from my grasp...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0),
(30226, 'Your deeds and commitment to this world\'s balance are exceptional. The dedication you show towards the guardians of nature sets an example any aspiring disciple of druidism should follow. The entirety of Moonglade and Hyjal will know of the crisis you averted this day. May your journeys be safe, $N.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);