INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (52129, 'This glyph is tailored to those whose love of adventure outweighs their desire to acquire power. Experience gains from creatures outside of dungeons will be reduced to 50% of normal, but for your patience and diligence, you will be rewarded every 10 levels with wonderful treasures. You will lose 5% of your current level\'s experience upon death, except in battlegrounds. This glyph can only be activated at Level 1.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (52130, 'This glyph is tailored to those who find the arduous journey more rewarding without stopping to rest. You will never again accumulate rested experience, but in exchange, you will master your weapon skills twice as fast.$B$BWhat\'s wrong? You don\'t see how this could be useful? Well, there\'s a rumor that those who reach the end of their journey exhausted and weary are those truly deserving of their achievement. Then again, that\'s just a rumor...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (52131, 'The armistice between the Horde and the Alliance is tenuous at best, and though mercenaries and adventurers such as yourself are wont to ally with whoever you wish regardless of race or faction, there still exists bad blood between those who recall the First and Second Wars. This glyph will raise your war banner for all to see, proclaiming that you will fight for the honor of your people. You will be permanently flagged for PvP until Level 60, but in return, you will gain 30% more EXP from all quests and kills.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
