REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (60701, 'You\'ve found an interesting looking feather.', 'You\'ve found an interesting looking feather.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61191, 'Yet, prisoners they are, good King.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61192, 'Humanity is in peril! The tides of darkness have come again, and the whole world is poised upon the brink of war.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61193, 'Hear me! The only hope for your people is to travel west, to the forgotten lands of Kalimdor!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61194, 'The dead in this land might lie still for the time being, but don\'t be fooled. Your young prince will find only death in the cold north.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61195, 'Commendable as that may be, his passions will be his undoing. It falls to you now, young sorceress.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61196, 'You must lead your people west to the ancient lands of Kalimdor. Only there can you combat the shadows and save this world from the flame.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61197, 'I know many things, young warchief, about you and your people. My identity, at this time, is unimportant. What is important is that you rally your people and leave these shores immediately.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61198, 'Human? I left my humanity behind long ago. I am something different now. ', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61199, 'Know that I have seen the future and beheld the great Burning Shadow that is coming to consume this world. You sense it as well, don\'t you?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61200, 'I am the reason for the Legion\'s return.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61201, 'Years ago, I brought the orcs into this world, and by doing so, I opened a path for the demons as well. For my sins I was murdered by those I cared for most. ', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61202, 'Despite my death, war raged across the lands of the east for many long years, leaving entire kingdoms devastated in its wake. ', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
REPLACE INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (61203, 'Now, at long last, I have returned to set things right. I am Medivh, the Last Guardian.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
